import Sample from "./Sample";
import Texture from "./Texture";

const JINGLE = 'data:audio/mp3;base64,';

const PRESS_A_KEY = 'data:image/jpg;base64,';

const SPLASH = 'data:image/jpg;base64,';

export const VERSION = '0.2.0';

export const FADE_FRAG = `
uniform sampler2D texture;
uniform mediump float fade;
uniform mediump float time;
varying highp vec2 vTextureCoord;
void main() {
    mediump vec4 flicker = vec4(sin(time) * 0.5 + 0.5, cos(time) * 0.5 + 0.5, tan(time) * 0.5 + 0.5, 1.0);
    gl_FragColor = texture2D(texture, vTextureCoord) * clamp(fade, 0.0, 1.0) * flicker;
}`;

/**
 * Creates a texture initialised to display the press a key text and returns it.
 * @param gl used to initialise the texture.
 * @returns the texture.
 */
export async function getPressAKeyTexture(
    gl: WebGLRenderingContext
): Promise<Texture> {
    const texture = new Texture();
    await texture.loadFromUrl(gl, PRESS_A_KEY);
    texture.setParameter(gl.TEXTURE_MAG_FILTER, gl.LINEAR);
    texture.setParameter(gl.TEXTURE_MIN_FILTER, gl.LINEAR);
    return texture;
}

/**
 * Creates a texture initialised to display the splash screen and returns it.
 * @param gl used to initialise the texture.
 * @returns the splash image texture.
 */
export async function getSplashTexture(
    gl: WebGLRenderingContext
): Promise<Texture> {
    const texture = new Texture();
    await texture.loadFromUrl(gl, SPLASH);
    texture.setParameter(gl.TEXTURE_MAG_FILTER, gl.LINEAR);
    texture.setParameter(gl.TEXTURE_MIN_FILTER, gl.LINEAR);
    return texture;
}

export async function getJingle(ac: AudioContext): Promise<Sample> {
    const sample = new Sample();
    await sample.loadFromUrl(ac, JINGLE);
    return sample;
}